import{_ as t,c as r,d as a,a as e,o as _,e as c}from"./app.1019bf5f.js";const o="/assets/quarry001.fa72e759.jpg",p="/assets/quarry003.78254d0f.jpg",n="/assets/quarry005.ef8f6cc4.jpg",i="/assets/quarry007.1be9e593.jpg",m="/assets/quarry009.5051efdf.jpg",g="/assets/quarry010.1f6b2f92.jpg",l="/assets/quarry013.e9aaf7a1.jpg",u="/assets/quarry014.f9520619.jpg",d="/assets/quarry015.c8665e46.jpg",f="/assets/quarry017.2e40cafe.jpg",V=JSON.parse('{"title":"Золотой день в карьере","description":"","frontmatter":{"title":"Золотой день в карьере","date":"2020-11-24","cover":"cover.png"},"headers":[],"relativePath":"fly/quarry/index.md"}'),y={name:"fly/quarry/index.md"},q=e('<p>Золотой день осени в октябре 2020. Полеты над Дзержинским карьером как способ понаблюдать за утихающей природой под необычным углом. Можно представить, как, видя подобные картины внизу, перелетные птицы собираются в свой долгий путь на юг.</p><p>В тот день я обошёл весь карьер вокруг, чувствуя кожей последние теплые лучи осеннего Солнца и вдыхая ароматы увядающего леса. Людные летом, эти места теперь опустели. В это время кто-то спокойно плавал на лодках между берегами, кто-то даже закалялся, купаясь в остывшей кристально чистой воде, а вокруг можно было погулять в тишине. Эти часы не для сложных размышлений — в голове было тихо, ум молчал и был заполнен созерцанием тонких деталей природного ландшафта.</p><p>За несколько вылетов я сумел запечатлеть то, что так непросто разглядеть с земли и чем спешу поделиться с каждым наблюдательным зрителем. Во время монтажа я раз за разом обнаруживал все новые мелкие интересные детали и тонкости в кадрах этого фильма. Присмотритесь и вы. Позвольте уму умолкнуть. Чтобы проследить потом, что первым всплывет в этой гулкой тишине изнутри.</p><p><img src="'+o+'" alt=""><img src="'+p+'" alt=""><img src="'+n+'" alt=""><img src="'+i+'" alt=""><img src="'+m+'" alt=""><img src="'+g+'" alt=""><img src="'+l+'" alt=""><img src="'+u+'" alt=""><img src="'+d+'" alt=""><img src="'+f+'" alt=""></p>',4);function j(h,x,T,b,v,w){const s=c;return _(),r("div",null,[a(s,{class:"my-24",link:"https://www.youtube.com/watch?v=Vwt57rGZUWg"}),q])}const N=t(y,[["render",j]]);export{V as __pageData,N as default};
